sys_variable_value.value:
(function execute(inputs, outputs) {
  //Build HTML for output to client
  var orderLines = JSON.parse(inputs.orderLines);
  var html = '<table cellspacing="0" cellpadding="0" width="50%">';
  var cost = 0;
  for (var i=0; i<orderLines.length; i++){
    var reqItemGr = new GlideRecord('sc_req_item');
    reqItemGr.get(orderLines[i].ritmNumber);
    //Item name
    html += '<tr><td colspan="2" style="font-weight: 600; background-color:#bdbdbd;">' + orderLines[i].itemDescription + '</td></tr>';
    //Price
    html += '<tr style="font-weight: 600; background-color:#dfdfdf;"><td>Price: </td><td>$' + orderLines[i].unitPrice + '<br></td></tr>';
    //Qty
    html += '<tr style="font-weight: 600; background-color:#dfdfdf;"><td>Quantity: </td><td>' + orderLines[i].quantity + '<br></td></tr>';
    cost += (parseInt(orderLines[i].unitPrice)*parseInt(orderLines[i].quantity));
  }
  //Total cost
  html += '<tr><td colspan="2" style="font-weight: 600; background-color:#edff85;">Total Before Shipping/Tax: ' + cost + '</td></tr>';
  html += '</table>';
  outputs.html = html;
})(inputs, outputs);

