sys_variable_value.value:
(function execute(inputs, outputs) {
  //Build HTML for output to client
  var orderLines = JSON.parse(inputs.orderLines);
  var html = '';
  var cost = 0;
  for (var i=0; i<orderLines.length; i++){
    var reqItemGr = new GlideRecord('sc_req_item');
    reqItemGr.get(orderLines[i].ritmNumber);
    //Item name
    html += '* **' + orderLines[i].itemDescription + '**';
    //Price
    html += ' - $' + orderLines[i].unitPrice + '';
    //Qty
    html += ' \* ' + orderLines[i].quantity + '\n';
    cost += (parseInt(orderLines[i].unitPrice)*parseInt(orderLines[i].quantity));
  }
  //Total cost
  html += '* **Total Before Shipping/Tax: $' + cost + '**';
  html += '';
  outputs.order_string = html;
})(inputs, outputs);

