sys_variable_value.value:
(function execute(inputs, outputs) {
    //Initialize Vars
    var orderLinesArr = [];
    var htmlOrderLinesArr = [];
    var orderLines = [];

    //Get Request
    var reqGr = new GlideRecord('sc_request');
    reqGr.get(inputs.request);

    //Get Ritms for request
    var ritmGr = new GlideRecord('sc_req_item');
    ritmGr.addQuery('request', reqGr.sys_id);
    ritmGr.query();

    //Add ritm info to array for processing
    while (ritmGr.next()) {
      //If the order already exists, increment it
      var cdwNumber = ritmGr.cat_item.model.upc.toString();
      var orderIndex = -1;
      if(orderLines.length!=0){
        for(var i=0; i<orderLines.length; i++){
          if(orderLines[i].cdw_item_number==cdwNumber)
          {
            orderIndex = i;
            break;
          }
        }
      }

      if(orderIndex == -1){
        orderLines.push({ 
          u_cdw_item: true, 
          number: ritmGr.number.toString(), 
          price: ritmGr.cat_item.price.getValue(), 
          quantity: ritmGr.quantity.getValue(),
          cdw_item_number: cdwNumber,
          short_description: ritmGr.cat_item.short_description.getValue()
        });
      }
      else{
        orderLines[orderIndex].quantity=(parseInt(orderLines[orderIndex].quantity)+parseInt(ritmGr.variables.quantity)).toString();
      }
    }

    //Build order line objects for the payload output and html in email
    for (var i = 0; i < orderLines.length; i++) {
      objLine = orderLines[i];
      if (orderLines[i].u_cdw_item == true) {
        //Payload order line
        var line = {
          unitPrice: objLine.price.toString(),
          manufacturerPartId: '',
          quantity: parseInt(objLine.quantity),
          cdwPartNumber: reduceStringSize(objLine.cdw_item_number,30),
          manufacturerName: reduceStringSize(objLine.cat_item.manufacturer_name,30),
          //requestDeliverydate: reqGr.u_requested_delivery_date.toString(),
          requestDeliverydate: '2022-04-26',
          uom: 'EA',
          contractId: '18496',
          unspscCode: '',
          itemDescription: reduceStringSize(objLine.short_description.replace('QTY:2',''),256),
          lineNumber: (i + 1).toString(),
          couponCode: ''
        };
        //HTML order line
        var htmlLine = {
          unitPrice: objLine.price.toString(),
          manufacturerPartId: '',
          quantity: parseInt(objLine.quantity),
          cdwPartNumber: reduceStringSize(objLine.cdw_item_number,30),
          manufacturerName: reduceStringSize(objLine.cat_item.manufacturer_name,30),
          //requestDeliverydate: reqGr.u_requested_delivery_date.toString(),
          requestDeliverydate: '2022-04-26',
          uom: 'EA',
          contractId: '18496',
          unspscCode: '',
          itemDescription: reduceStringSize(objLine.short_description.replace('QTY:2',''),256),
          lineNumber: (i + 1).toString(),
          couponCode: '',
          ritmNumber: objLine.number
        };
        //Push order linse to array
        orderLinesArr.push(line);
        htmlOrderLinesArr.push(htmlLine);
      }
    }

    //Set output vars as JSON strings
    outputs.orderlines = JSON.stringify(orderLinesArr);
    outputs.htmlorderlines = JSON.stringify(htmlOrderLinesArr);

    //reduce the length of strings to avoid API string length issues
    function reduceStringSize(inString,length){
      var outString = '';
      if(inString.length > length) {
        outString = inString.substring(0,length);
      }
      else{
        outString = inString;
      }
      return outString;
    }
  })(inputs, outputs);

