sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var dateTimeNow = new GlideDateTime();
	dateTimeNow = dateTimeNow.getDate();
	var reqGr = new GlideRecord('sc_request');
	reqGr.get(current.request.sys_id.toString());
	reqGr.delivery_address = current.variables.address_1;
	reqGr.u_address_1 = current.variables.address_1;
	reqGr.u_address_2 = current.variables.address_2;
	reqGr.u_address_city = current.variables.city;
	reqGr.u_address_phone = current.variables.phone_number;
	reqGr.u_address_state = current.variables.state;
	reqGr.u_address_zip = current.variables.zip_code;
	reqGr.u_company = current.variables.company;
	reqGr.u_location = current.variables.location;
	reqGr.u_order_comment = current.variables.chargeback_and_configuration;
	reqGr.u_requested_delivery_date = dateTimeNow.toString();
	reqGr.short_description = "Hardware Order";
	reqGr.u_cdw_order = true;
	reqGr.update();

	try{
		var reqGr = new GlideRecord('sc_request');
		reqGr.get(current.request.sys_id.toString());
		var inputs = {};
		inputs['current'] = reqGr; // GlideRecord of table:  
		//inputs['changed_fields'] = {}; // Array.Object 
		inputs['table_name'] = 'sc_request';
		sn_fd.FlowAPI.getRunner().flow('global.cdw_ordering_new_process').inBackground().withInputs(inputs).run();
	}
	catch(e){
		gs.warn(e);
	}
})(current, previous);
